pub fn terrainUniformOffset(ub_name: []const u8, u_name: []const u8) ?usize {
    if (std.mem.eql(u8, ub_name, "vs_params")) {
        if (std.mem.eql(u8, u_name, "base_color")) {
            return 0;
        }
        if (std.mem.eql(u8, u_name, "noise_frequency")) {
            return 16;
        }
        if (std.mem.eql(u8, u_name, "noise_amplitude")) {
            return 20;
        }
        if (std.mem.eql(u8, u_name, "mvp")) {
            return 32;
        }
    }
    return null;
}
